$(document).ready(function()
{$("nav").find("li").on("click","a",function()
{$('.navbar-collapse.in').collapse('hide')});$("#go-top-btn").on('click',function()
{goTop()});setInterval(function(){changeCarouselImgBasedScreen()},2000);$('#carousel-post').carousel({interval:4000})
$('.card .carousel-item').each(function(){var next=$(this).next();if(!next.length){next=$(this).siblings(':first')}
next.children(':first-child').clone().appendTo($(this));for(var i=0;i<2;i++){next=next.next();if(!next.length){next=$(this).siblings(':first')}}
next.children(':first-child').clone().appendTo($(this))});initMap()});$(".amount-btn").on("click",function()
{$(".amount-btn.active-amount").removeClass("active-amount");$(this).addClass("active-amount");donateAmount()})
var selectLang=document.getElementById("langselector");selectLang.addEventListener("change",function()
{var value=selectLang.value;switch(value)
{case "en":loadTranslator("en");break;case "gr":loadTranslator("gr");break}});function initMap(){var uluru={lat:37.971996112,lng:23.73416373};var map=new google.maps.Map(document.getElementById('map'),{zoom:14,center:uluru});var marker=new google.maps.Marker({position:uluru,map:map})}
function goTop()
{document.body.scrollTop=0;document.documentElement.scrollTop=0}
function Translate(){this.init=function(attribute,lng){this.attribute=attribute;this.lng=lng}
this.process=function(){var _self=this;var xrhFile;if(window.XMLHttpRequest)
{xrhFile=new XMLHttpRequest()}
else{xrhFile=new ActiveXObject("Microsoft.XMLHTTP")}
xrhFile.open("GET","./assets/data/"+this.lng+".json",!1);xrhFile.onreadystatechange=function()
{if(xrhFile.readyState===4)
{if(xrhFile.status===200||xrhFile.status==0)
{var LngObject=JSON.parse(xrhFile.responseText);var allDom=document.getElementsByTagName("*");for(var i=0;i<allDom.length;i++)
{var elem=allDom[i];var inputs=document.getElementsByTagName.input;var key=elem.getAttribute(_self.attribute);if(key!=null)
{elem.innerHTML=LngObject[key]}}}}}
xrhFile.send()}}
function loadTranslator(lang)
{var translate=new Translate();var currentLng=lang;var attributeName='data-tag';translate.init(attributeName,currentLng);translate.process()}
function openModal(){document.getElementById('myModal').style.display="block"}
function closeModal(){document.getElementById('myModal').style.display="none"}
var slideIndex=1;showSlides(slideIndex);function plusSlides(n){showSlides(slideIndex+=n)}
function currentSlide(n){showSlides(slideIndex=n)}
function showSlides(n){var i;var slides=document.getElementsByClassName("mySlides-lightbox");if(n>slides.length){slideIndex=1}
if(n<1){slideIndex=slides.length}
for(i=0;i<slides.length;i++){slides[i].style.display="none"}
slides[slideIndex-1].style.display="block"}
function closeModalMsg(){document.getElementById('myModal-thankmsg').style.display="none"}
function donateAmount(){if($("input[name='otheramount']").val().length>0){("input[name='amount']").val($("input[name='otheramount']").val())}else{$("input[name='amount']").val($("button.amount-btn.active-amount").val())}}
function donateType()
{if($('#donationFrequency1').is(':checked'))
{$("input[name='cmd']").val("_donations")}
else{$("input[name='cmd']").val("_xclick-subscriptions")}}
function changeCarouselImgBasedScreen()
{var isIE=!1||!!document.documentMode;var screenWidth=window.innerWidth||document.documentElement.clientWidth||document.body.clientWidth;var slideImgFirst=document.getElementById("slideImgFirst");var slideImgSecond=document.getElementById("slideImgSecond");var slideImgThird=document.getElementById("slideImgThird");if(isIE)
{if(screenWidth>=1300)
{slideImgFirst.src="assets/images/slider_3.jpg";slideImgSecond.src="assets/images/slider_2.jpg";slideImgThird.src="assets/images/slider_1.jpg"}
else if(screenWidth>=701&&screenWidth<1300)
{slideImgFirst.src="assets/images/slider_medium_3.jpg";slideImgSecond.src="assets/images/slider_medium_2.jpg";slideImgThird.src="assets/images/slider_medium_1.jpg"}
else if(screenWidth<=700)
{slideImgFirst.src="assets/images/slider_mobile_3.jpg";slideImgSecond.src="assets/images/slider_mobile_2.jpg";slideImgThird.src="assets/images/slider_mobile_1.jpg"}}}